import './App.css';

class WeatherApp extends Component{

    constructor(props)
    {
        super(props);
        this.state = {
            temp:0,
            desc:'',
            icon:'',
            loading: true
        }
    }

    componentDidMount() {
        fetch('http://api.openweathermap.org/data/2.5/weather?q=London&units=Metric&APIkey=10a171f667c3712637da275f0e52a733')
        .then(response => response.json())
        .then(responseData => {
            this.setState(
                {
                temp: responseData.main.temp,
                desc: responseData.weather[0].description,
                icon: responseData.weather[0].icon,
                loading: false
                });
        });


    }

    render() {

        const imgSrc = 'http://openweathermap.org/img/w/' + 
    this.state.icon + '.png';

    if (this.state.loading) {
        return <p>Loading</p>;
      }
