        return(
            <div className="App">
                <p>Temperature: {this.state.temp} Â°C</p>
          <p>Description: {this.state.desc}</p>
          <img src={imgSrc} alt="Weather icon" />

            </div>
        );

      }

        
    }

}



export default WeatherApp;
